import './App.css';
import Home from './component/Home'
import About from './component/About'
import Login from './component/Login'

import AddUser from './component/AddUser'
import UserList from './component/UserList'
import  {AppContext} from './component'

import{Route,BrowserRouter as Router ,Switch,Link} from 'react-router-dom'
import { useState } from 'react';


export default function App() {


const [users,setUsers]=useState([]);


const dispatchUserEent =(actionType ,payload) =>{

  switch(actionType){

      case'ADD_USER':
      setUsers([...users,payload.newUser])
      return;

        case 'REMOVER_USER':
        setUsers(users.filter(user=>user.id !== payload.userId))
        return;

        default:

        return;


  }
}




  return (
     <>


<Router>
<nav>
<ul>
 <li><Link to ='/' exact> Home</Link></li>
 <li><Link to ='/About'>About</Link></li>
 <li><Link to ='/Login' >Login </Link></li>

</ul>

</nav>
<Switch>

<Route path ='/' exact component={Home}/>
<Route path ='/About' component={About}/>
<Route path ='/Login' component={Login}/>

    </Switch>


</Router>


<div className='App'>

  <AppContext.Providor value={{users,dispatchUserEent}}>

    <AddUser/>
    <UserList/>

  </AppContext.Providor>
</div>
     </>
  );
}
